import React,{useContext,forwardRef,createRef} from 'react';
import '../App.css';
import ShapeComponent from './shexComponents/ShapeComponent';
import {ShapesContext} from '../App';

function AssistantComp (props) {

    const context = useContext(ShapesContext);
    let domRefs = {};


    const wtf = function(){
        console.log(domRefs[0])
    }

    const add = function(shape,ref){
        console.log(ref)
        domRefs[shape.id] = ref
        return ref;
    }


    const ref = createRef();
    return (<div id='assistant-container'> 

                    <div className='col row assisTitleDiv'>
                        <span className="assisTitle">Assistant</span>
                    </div>
                    {context.shapes.map(shape =>{
                           return <ShapeComponent ref={forwardRef((props,ref)=>{add(shape,props)})} key={shape.id} shape={shape}/>
                        })
                    }

                    <button id='addShapeButton' 
                            className="btn-primary addShapeButton"
                            onClick={wtf}>
                            + Shape
                    </button>
                </div>);


}

export default AssistantComp;
