                    ref={(ref) => {domRefs[shape.id] = ref}} 
                    shape={shape}/>);
    }

    return (<div id='assistant-container'> 

                    <div className='col row assisTitleDiv'>
                        <span className="assisTitle">Assistant</span>
                    </div>
                    {context.shapes.map(shape =>{
